import React, {useState} from "react";
import {PageContainer, FooterToolbar} from '@ant-design/pro-layout';
import {DownOutlined, HeartOutlined, PlusOutlined, UserOutlined} from '@ant-design/icons';
import {Progress, Tag, Button, Space, Badge, Typography, Image, Row, Col} from 'antd';
import ProList from '@ant-design/pro-list';
import {queryData} from "@/components/Common/crud";
import {getDBImage} from "@/pages/DataBase/DB";
import ChooseDB from "./components/ChooseDB";

const {Text} = Typography;

const url = '/api/database';

const DataBaseTableList: React.FC<{}> = (props: any) => {

  const [chooseDBModalVisible, handleChooseDBModalVisible] = useState<boolean>(false);

  const chooseDB = () =>{

  };

  return (
    <PageContainer>
      <ProList
        toolBarRender={() => {
          return [
            <Button type="primary" onClick={() => handleChooseDBModalVisible(true)}>
              <PlusOutlined/> 新建
            </Button>,
          ];
        }}
        pagination={{
          defaultPageSize: 8,
          showSizeChanger: false,
        }}
        grid={{gutter: 16, column: 4}}
        request={(params, sorter, filter) => queryData(url, {...params, sorter: {id: 'descend'}, filter})}
        metas={{
          title: {
            dataIndex: 'alias',
            title: 'alias',
          },
          subTitle: {
            render: (_, row) => {
              return (
                <Space size={0}>
                  <Tag color="blue" key={row.name}>
                    {row.name}
                  </Tag>
                  <Tag color="gray" key={row.groupName}>
                    {row.groupName}
                  </Tag>
                  {(row.status) ?
                    (<><Badge status="success"/><Text type="success">正常</Text></>) :
                    <><Badge status="error"/><Text type="danger">异常</Text></>}
                </Space>
              );
            },
          },
          type: {},
          avatar: {},
          content: {
            render: (_, row) => {
              return (
                <Row justify="space-around" align="middle">
                  <Col span={16}>
                    <Image
                      height={120}
                      preview={false}
                      src={getDBImage(row.type)}
                      fallback="data:image/png;base64,"
                    />
                  </Col>
                </Row>
              )
            },
          },
          actions: {},
        }}
        headerTitle="数据源"
      />
      <ChooseDB onCancel={() => handleChooseDBModalVisible(false)} modalVisible={chooseDBModalVisible}/>
    </PageContainer>
  );
};


export default DataBaseTableList;
